<?php$current_options = get_option( 'busiprof_pro_theme_options' );if(isset($_POST['busiprof_settings_save_3'])){			if($_POST['busiprof_settings_save_3'] == 1) 	{		if ( empty($_POST) || !wp_verify_nonce($_POST['busiprof_gernalsetting_nonce_customization'],'busiprof_customization_nonce_gernalsetting') )		{			print 'Sorry, your nonce did not verify.';			exit;		}		else  		{			$current_options['footer_copyright_text']=$_POST['footer_copyright_text'];						update_option('busiprof_pro_theme_options' , $current_options);		}     }		 if($_POST['busiprof_settings_save_3'] == 2) 	 {		do_action('busiprof_restore_data','3');	 } } ?><form method="post" action = ""  id="theme_options_home_3">	<?php wp_nonce_field('busiprof_customization_nonce_gernalsetting','busiprof_gernalsetting_nonce_customization'); ?>	<div class="postbox" id="Basic_setting_3">		<div title="Click to toggle" class="handlediv"><br></div>						<div class="inside">		<p><h4 class="heading"><?php _e('Footer Customization text','busi_prof'); ?></h4>			<textarea rows="5" cols="80" name="footer_copyright_text" id="footer_copyright_text" ><?php if(isset($current_options['footer_copyright_text'])) 			{ esc_attr_e($current_options['footer_copyright_text']); } ?> </textarea>			<span class="icon help">					<span class="tooltip"><?php  _e('Enter your Footer Customization text','busi_prof');?></span>			</span>					</div>		</div>	<!---DATA SAVE------>    <div id="busiprof_optionsframework-submit">         		<input type="hidden" value="1" id="busiprof_settings_save_3" name="busiprof_settings_save_3" />		<input type="button" class="button-primary"  value= "<?php _e('Save Changes', 'busi_prof');?>" onclick="datasave_home('3')"/>											<input type="button" class="reset-button button-secondary"  value="<?php _e('Restore Defaults','busi_prof');?>" onclick="reset_data_home('3')" />		<div style="display:none; margin-left:300px; margin-top:-25px; padding:10px; color: #468847; background-color: #DFF0D8; border-color: #D6E9C6;" id="busiprof_settings_save_3_reset" ><?php _e('Options data successfully reset','busi_prof');?></div>		<div style="display:none; margin-left:300px; margin-top:-25px; padding:10px; color: #468847; background-color: #DFF0D8; border-color: #D6E9C6;" id="busiprof_settings_save_3_success" ><?php _e('Options data successfully Saved','busi_prof');?></div>	</div></form>