current_options['home_banner_strip_enabled']= sanitize_text_field($_POST['home_banner_strip_enabled']); } 			else { echo $current_options['home_banner_strip_enabled']="off"; }						if(isset($_POST['read_more_btn_enabled']))			{ echo $current_options['read_more_btn_enabled']= sanitize_text_field($_POST['read_more_btn_enabled']); } 			else { echo $current_options['read_more_btn_enabled']="off"; }					update_option('busiprof_pro_theme_options' , $current_options);		}     }		 if($_POST['busiprof_settings_save_1'] == 2) 	 {		do_action('busiprof_restore_data','1');	 } } ?><form method="post" action = ""  id="theme_options_home_1">	<?php wp_nonce_field('busiprof_customization_nonce_gernalsetting','busiprof_gernalsetting_nonce_customization'); ?>	<div class="postbox" id="Basic_setting_1">		<div title="Click to toggle" class="handlediv"><br></div>				<h3 class="hndle"><span><?php _e('Custom Static Front Page','busi_prof');?><span class="postbox-title-action">		</h3>		<div class="inside">									<p><h4 class="heading"><?php _e('Do you want display Static Front Page: ','busi_prof');?></h4>				<?php $front_page = $current_options['front_page']; ?>				<?php _e('Yes','busi_prof');?> 				<input type="radio" name="front_page" value="yes" <?php if($front_page == 'yes') echo "checked"; ?> >								<a href="<?php echo admin_url('/options-reading.php'); ?>"><?php _e('front page option','busi_prof'); ?></a>				&nbsp;&nbsp;				<input type="radio" name="front_page" value="no" <?php if($front_page == 'no') echo "checked";  ?> >  <?php _e('No','busi_prof');?>  &nbsp;&nbsp;				<span class="icon help">					<span class="tooltip"><?php  _e('Shows Custom Static Front Page','busi_prof');?></span>				</span>			</p>					</div>		</div>	<div class="postbox" id="Basic_setting_1">		<div title="Click to toggle" class="handlediv"><br></div>				<h3 class="hndle"><span><?php _e('Custom Logo','busi_prof');?><span class="postbox-title-action">		</h3>		<div class="insi