<?php  $current_options=get_option('spa_theme_options');  if(isset($_POST['spasalon_settings_save_4']))  {		  if($_POST['spasalon_settings_save_4'] == 1)   {  	/*nonce field implement*/  	if ( empty($_POST) || !wp_verify_nonce($_POST['spa_footer_nonce_customization'],'spa_customization_nonce_footer') )  	{  	   print 'Sorry, your nonce did not verify.';  	   exit;  	}  	else   	{  		$current_options['footer_tagline']=sanitize_text_field($_POST['footer_tagline']);  		$current_options['footer_url']=sanitize_text_field($_POST['footer_url']);  		$current_options['footer_designedby']=sanitize_text_field($_POST['footer_designedby']);  		  		update_option('spa_theme_options' ,stripslashes_deep($current_options));  		  	}     }     if($_POST['spasalon_settings_save_4'] == 2)    {  	do_action('spa_restore_data', 2);		   }  }      ?><form method="post" action = ""  id="theme_options_home_4">  <?php wp_nonce_field('spa_customization_nonce_footer','spa_footer_nonce_customization'); ?>  <div class="postbox" id="spasalon_footer_custmization">    <div title="Click to toggle" class="handlediv"><br></div>    <h3 class="hndle">      <span>        <?php _e('Footer Copyrights','sis_spa');?>         <span class="postbox-title-action">    </h3>    <div class="inside">    <p><h4 class="heading"><?php _e('Footer Tagline','sis_spa');?></h4>    <input type="text" class="inputwidth" name="footer_tagline" id="footer_tagline" value="<?php if($current_options['footer_tagline']!='') { echo esc_attr($current_options['footer_tagline']); } ?>"/>    <span class="icon help">    <span class="tooltip"><?php  _e('Enter Footer Tagline','sis_spa');?></span></span>    </span>    </p>    <p><h4 class="heading"><?php _e('Website By','sis_spa');?></h4>    <input type="text" class="inputwidth" name="footer_designedby" id="footer_designedby" value="<?php if($current_options['footer_designedby']!='') { echo esc_attr($current_options['footer_designedby']); } ?>"/>    <span class="icon help">    <span class="tooltip"><?php  _e('Enter Website Owner Title','sis_spa');?></span></span>    </span>    </p>    <p><h4 class="heading"><?php _e('Footer URL','sis_spa');?></h4>    <input type="text" class="inputwidth" name="footer_url" id="footer_url" value="<?php if($current_options['footer_url']!='') { echo esc_attr($current_options['footer_url']); } ?>"/>    <span class="icon help">    <span class="tooltip"><?php  _e('Enter Footer URL','sis_spa');?></span></span>    </span>    </p>    </div>	  </div>  <!---DATA SAVE------>  <div id="busiprof_optionsframework-submit">    <input type="hidden" value="1" id="spasalon_settings_save_4" name="spasalon_settings_save_4" />    <input type="button" class="button-primary"  value= "<?php _e('Save Changes', 'sis_spa');?>" onclick="datasave_home('4')"/>									    <input type="button" class="reset-button button-secondary"  value="<?php _e('Restore Defaults','sis_spa');?>" onclick="reset_data_home('4')" />    <div id="success_message_reset_4" style="display:none;width:300px;">      <?php _e('Data reset sucessfully','sis_spa');?>    </div>    <div id="success_message_save_4" style="display:none;width:300px;">      <?php _e('Data save sucessfully','sis_spa');?>    </div>  </div></form>